// src/components/RateCardModal.js
import React, { useState } from 'react';
import { FaInfoCircle } from 'react-icons/fa'; // Import the icon for the button
import { FaWhatsapp } from 'react-icons/fa'; // Import WhatsApp icon

const RateCardModal = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="relative">
      {/* Button with Icon */}
      <button
        onClick={toggleModal}
        className="fixed bottom-6 right-6 bg-teal-500 text-white p-3 rounded-full shadow-lg hover:bg-teal-600 focus:outline-none z-50"
      >
        <FaInfoCircle size={24} />
      </button>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed bottom-16 right-6 bg-gray-800 bg-opacity-60 flex justify-center items-center z-40">
          <div className="bg-gradient-to-r from-indigo-600 to-teal-600 w-80 rounded-lg p-6 shadow-xl">
            <h2 className="text-center text-2xl font-bold text-white mb-4">Rate Card & Information</h2>
            <div className="text-center mb-4 text-white">
              <p className="text-lg font-medium mb-2">üí∞ Deposit: ‚Çπ100</p>
              <p className="text-lg font-medium mb-2">üèÜ Winning: ‚Çπ185</p>
              <p className="text-sm mb-2">üìâ 15% Deduction on Transaction & Winning Prize</p>
              <p className="text-md mt-4">For more details and support, join our WhatsApp group:</p>
              <div className="flex justify-center items-center mt-4">
                <a
                  href="https://chat.whatsapp.com/KCwLBp6RVn9FgyX9xDvJdN"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-green-500 text-white px-4 py-2 rounded-md flex items-center space-x-2 hover:bg-green-600"
                >
                  <FaWhatsapp size={20} />
                  <span>Join WhatsApp Group</span>
                </a>
              </div>
            </div>
            <div className="text-center">
              <button
                onClick={toggleModal}
                className="mt-4 px-4 py-2 bg-teal-500 text-white rounded-md w-full hover:bg-teal-600 focus:outline-none"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RateCardModal;
