import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import successImg from "../../images/successImg.png"; // Assuming you have an image for success
import { Fragment } from "react"; // Correct import

const EmailVerify = () => {
  const [validUrl, setValidUrl] = useState(true);
  const [loading, setLoading] = useState(true); // Added loading state
  const param = useParams();

  useEffect(() => {
    const verifyEmailUrl = async () => {
      try {
        const url = `http://localhost:8080/api/users/${param.id}/verify/${param.token}`;
        const { data } = await axios.get(url);
        console.log(data); // For debugging
        setValidUrl(true);
      } catch (error) {
        console.error(error);
        setValidUrl(false);
      } finally {
        setLoading(false); // Set loading to false when done
      }
    };
    verifyEmailUrl();
  }, [param]);

  return (
    <Fragment>
      {loading ? (
        <div className="flex justify-center items-center h-screen text-gray-600">
          <div className="text-xl">Verifying your email...</div>
        </div>
      ) : validUrl ? (
        <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-xl flex flex-col items-center">
          <img src={successImg} alt="Success" className="w-32 h-32 mb-6" />
          <h1 className="text-2xl font-semibold text-green-600 mb-4">
            Email Verified Successfully!
          </h1>
          <p className="text-gray-600 mb-6 text-center">
            Your email has been successfully verified. You can now log in to your account.
          </p>
          <Link to="/login">
            <button className="w-full py-2 px-4 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition duration-200">
              Go to Login
            </button>
          </Link>
        </div>
      ) : (
        <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-xl text-center">
          <h1 className="text-xl text-red-500 mb-4">
            Oops! The verification link is invalid or expired.
          </h1>
          <p className="text-gray-600 mb-4">
            Please check your email for a new verification link or contact support if needed.
          </p>
          <Link to="/contact">
            <button className="mt-4 py-2 px-4 bg-red-500 text-white font-semibold rounded-lg hover:bg-red-600 transition duration-200">
              Contact Support
            </button>
          </Link>
        </div>
      )}
    </Fragment>
  );
};

export default EmailVerify;
