// src/components/SupportButton.js
import React, { useState } from 'react';
import { FaHeadset } from 'react-icons/fa'; // Support/Help icon (headset)

const SupportButton = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="relative">
      {/* Support Icon Button */}
      <button
        onClick={toggleModal}
        className="fixed bottom-16 right-6 bg-gradient-to-r from-teal-400 to-blue-500 text-white p-4 rounded-full shadow-lg hover:bg-gradient-to-r hover:from-teal-500 hover:to-blue-600 focus:outline-none z-50 transition duration-300"
      >
        <FaHeadset size={28} />
      </button>

      {/* Modal for Support */}
      {isModalOpen && (
        <div className="fixed bottom-13 right-6 bg-white shadow-lg rounded-lg w-96 p-6 z-40 transition transform duration-300 ease-in-out">
          <div className="relative">
            {/* Close Icon */}
            <button
              onClick={toggleModal}
              className="absolute top-2 right-2 text-gray-600 text-2xl hover:text-gray-400"
            >
              ✖️
            </button>
            <h2 className="text-center text-2xl font-semibold text-gray-800 mb-4">Need Support?</h2>
            <div className="text-center text-gray-700">
              <p className="mb-4">We're here to help! Please feel free to contact us for any support or assistance.</p>
              <p className="text-md mt-4">You can reach out to us by visiting our contact page:</p>
              <div className="flex justify-center items-center mt-4">
                <a
                  href="/contact" // Link to your contact page
                  className="bg-teal-500 text-white px-5 py-2 rounded-md hover:bg-teal-600 transition duration-200"
                >
                  Go to Contact Page
                </a>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SupportButton;
