// src/components/SupportButton.js
import React, { useState } from 'react';
import { FaHeadset } from 'react-icons/fa'; // Support/Help icon (headset)

const SupportButton = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="relative">
      {/* Support Icon Button */}
      <button
        onClick={toggleModal}
        className="fixed bottom-6 left-6 bg-blue-500 text-white p-3 rounded-full shadow-lg hover:bg-blue-600 focus:outline-none z-50"
      >
        <FaHeadset size={24} />
      </button>

      {/* Modal for Support */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-40">
          <div className="bg-gradient-to-r from-blue-600 to-indigo-600 w-80 rounded-lg p-6 shadow-xl relative">
            {/* Close Icon */}
            <button
              onClick={toggleModal}
              className="absolute top-4 right-4 text-white text-xl hover:text-gray-300"
            >
              ✖️
            </button>
            <h2 className="text-center text-2xl font-semibold text-white mb-4">Need Support?</h2>
            <div className="text-center text-white">
              <p className="mb-4">We're here to help! Please feel free to contact us for any support or assistance.</p>
              <p className="text-md mt-4">You can also reach us via our WhatsApp group:</p>
              <div className="flex justify-center items-center mt-4">
                <a
                  href="https://wa.me/yourwhatsapplink"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-green-500 text-white px-4 py-2 rounded-md flex items-center space-x-2 hover:bg-green-600"
                >
                  <span>Join WhatsApp Group</span>
                </a>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SupportButton;
