import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [data, setData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate(); // For programmatic navigation

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:8080/api/auth";
      const { data: res } = await axios.post(url, data);
      localStorage.setItem("token", res.data); // Store the JWT token
      navigate("/"); // Redirect to home page after successful login
    } catch (error) {
      if (error.response && error.response.status >= 400 && error.response.status <= 500) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    <div className="min-h-screen bg-cover bg-center relative" style={{ backgroundImage: "url('https://images.unsplash.com/photo-1602534391811-0548f7e9e3c7')" }}>
      <div className="absolute inset-0 bg-gradient-to-b from-gray-800 via-gray-900 to-black opacity-75"></div>
      <div className="flex justify-center items-center min-h-screen px-6">
        <div className="bg-gray-800 bg-opacity-90 p-10 rounded-xl shadow-xl max-w-lg w-full">
          <div className="text-center mb-8">
            <h1 className="text-5xl text-transparent bg-clip-text bg-gradient-to-r from-cyan-500 via-purple-500 to-pink-500 font-extrabold mb-4">
              Welcome Back, Gamer!
            </h1>
            <p className="text-lg text-gray-300 mb-6">Please log in to your account to continue</p>
            <Link to="/signup">
              <button className="px-6 py-3 text-lg text-white bg-gradient-to-r from-indigo-500 via-blue-500 to-teal-500 rounded-md hover:bg-gradient-to-l hover:from-indigo-400 hover:to-teal-400 transition-all duration-300 ease-in-out">
                Create an Account
              </button>
            </Link>
          </div>
          <form onSubmit={handleSubmit} className="space-y-6">
            <h2 className="text-4xl text-white font-semibold text-center mb-6">Login to Your Account</h2>
            <div className="relative">
              <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={handleChange}
                value={data.email}
                className="w-full px-4 py-3 rounded-lg text-white bg-gray-700 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition-all ease-in-out"
                required
              />
            </div>
            <div className="relative">
              <input
                type="password"
                name="password"
                placeholder="Password"
                onChange={handleChange}
                value={data.password}
                className="w-full px-4 py-3 rounded-lg text-white bg-gray-700 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-500 transition-all ease-in-out"
                required
              />
            </div>
            {error && <div className="text-red-600 text-sm">{error}</div>}
            <button
              type="submit"
              className="w-full py-3 bg-gradient-to-r from-teal-400 via-blue-500 to-indigo-600 text-white font-semibold rounded-lg hover:opacity-90 transition-all duration-200 ease-in-out"
            >
              Sign In
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
