// src/components/RateCard.js
import React from 'react';

const RateCard = () => {
  return (
    <div className="fixed bottom-6 right-6 bg-white shadow-lg rounded-lg p-4 max-w-xs w-60 border-2 border-gray-200">
      <h3 className="text-xl font-semibold text-center text-gray-800">Rate Card</h3>
      
      <div className="my-4 text-center">
        <p className="text-lg font-medium text-gray-700">Deposit: ₹100</p>
        <p className="text-lg font-medium text-gray-700">Winning: ₹185</p>
        <p className="text-sm text-gray-500">15% Deduction on Transaction & Winning</p>
      </div>

      <div className="text-center">
        <button className="px-4 py-2 bg-blue-500 text-white rounded-md w-full hover:bg-blue-600 focus:outline-none">
          Play Now
        </button>
      </div>
    </div>
  );
};

export default RateCard;
