// src/components/RateCardModal.js
import React, { useState } from 'react';
import { FaInfoCircle } from 'react-icons/fa'; // Import the icon for the button

const RateCardModal = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="relative">
      {/* Button with Icon */}
      <button
        onClick={toggleModal}
        className="fixed bottom-6 right-6 bg-blue-500 text-white p-3 rounded-full shadow-lg hover:bg-blue-600 focus:outline-none z-50"
      >
        <FaInfoCircle size={24} />
      </button>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed bottom-16 right-6 bg-gray-800 bg-opacity-50 flex justify-center items-center z-40">
          <div className="bg-white w-72 rounded-lg p-6 shadow-lg">
            <h2 className="text-center text-xl font-semibold mb-4">Rate Card</h2>
            <div className="text-center mb-4">
              <p className="text-lg font-medium text-gray-700">Deposit: ₹100</p>
              <p className="text-lg font-medium text-gray-700">Winning: ₹185</p>
              <p className="text-sm text-gray-500">15% Deduction on Transaction & Winning</p>
            </div>
            <div className="text-center">
              <button
                onClick={toggleModal}
                className="px-4 py-2 bg-blue-500 text-white rounded-md w-full hover:bg-blue-600 focus:outline-none"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RateCardModal;
